-- Tabla: administradores
CREATE TABLE `administradores` (`id` INT(11) PRIMARY KEY, `nombre` VARCHAR(255) NOT NULL, `email` VARCHAR(255) NOT NULL UNIQUE, `password` VARCHAR(255) NOT NULL, `rol` VARCHAR(255) NOT NULL DEFAULT 'admin', `activo` TINYINT(1) NOT NULL DEFAULT 1, `ultimo_acceso` DATETIME, `creado_por` INT(11), `fecha_creacion` DATETIME NOT NULL, `fecha_actualizacion` DATETIME NOT NULL);

-- Tabla: auditoria
CREATE TABLE auditoria (
                id INT(11) NOT NULL AUTO_INCREMENT PRIMARY KEY,
                tabla TEXT NOT NULL,
                operacion TEXT NOT NULL,
                registro_id INT(11),
                datos_anteriores TEXT,
                datos_nuevos TEXT,
                usuario TEXT DEFAULT 'sistema',
                fecha DATETIME DEFAULT CURRENT_TIMESTAMP
            );

-- Tabla: fiscal
CREATE TABLE `fiscal` (`id` INT(11) PRIMARY KEY, `nombre` TEXT NOT NULL, `email` TEXT NOT NULL UNIQUE, `cel-num` TEXT, `direccion` TEXT NOT NULL, `zona` INT(11) NOT NULL, `institucion` TEXT, `password` TEXT, `re-password` TEXT, `tipo` VARCHAR(255) NOT NULL DEFAULT 'fiscal', `fiscal_general_asignado` INT(11) REFERENCES `fiscal` (`id`) ON DELETE SET NULL ON UPDATE CASCADE, `institucion_id` INT(11) REFERENCES `instituciones` (`id`));

-- Tabla: instituciones
CREATE TABLE `instituciones` (`id` INT(11) NOT NULL AUTO_INCREMENT PRIMARY KEY, `nombre` VARCHAR(255) NOT NULL UNIQUE, `direccion` TEXT NOT NULL, `zona` INT(11) NOT NULL, `telefono` VARCHAR(255), `email` VARCHAR(255), `tipo` TEXT NOT NULL DEFAULT 'centro_votacion', `activa` TINYINT(1) NOT NULL DEFAULT 1, `capacidad_votantes` INT(11), `observaciones` TEXT, `fecha_creacion` DATETIME NOT NULL, `fecha_actualizacion` DATETIME NOT NULL);

